parameters.jsCode:
// Split Clay response into individual lead items
const clayResponse = $input.first().json;
const leads = clayResponse.leads || [];

if (leads.length === 0) {
  return [{ json: { message: 'No new leads found' } }];
}

return leads.map(lead => ({ json: lead }));

// Apply industry-based square footage heuristics if missing
const lead = $input.first().json;

// Default square footage by industry type
const sqftDefaults = {
  'School': 50000,
  'Retail': 15000,
  'Restaurant': 5000,
  'Office': 25000,
  'Municipality': 100000,
  'Franchise': 10000
};

let squareFootage = lead.square_footage;
let needsManualReview = false;

// If square footage is missing, apply heuristic
if (!squareFootage || squareFootage === null || squareFootage === 0) {
  const baseDefault = sqftDefaults[lead.type] || 20000;
  const numLocations = lead.num_locations || 1;
  squareFootage = baseDefault * numLocations;
  needsManualReview = true;
}

return [{
  json: {
    ...lead,
    square_footage: squareFootage,
    sqft_source: needsManualReview ? 'heuristic' : 'enrichment',
    needs_manual_review: needsManualReview
  }
}];

// Validate utility provider eligibility
const lead = $input.first().json;
const utilities = $input.last().json;

// Extract eligible utility names
const eligibleUtilities = utilities
  .filter(u => u.Status === 'Active')
  .map(u => ({
    name: u['Utility Name'],
    state: u.State,
    rate: u.All_In_Rate
  }));

// Check if lead's utility is eligible
const matchedUtility = eligibleUtilities.find(
  u => u.name.toLowerCase() === lead.utility_provider?.toLowerCase() &&
       u.state === lead.state
);

if (!matchedUtility) {
  return [{
    json: {
      ...lead,
      eligible: false,
      rejection_reason: `Utility provider "${lead.utility_provider}" not eligible in ${lead.state}`,
      status: 'Rejected'
    }
  }];
}

return [{
  json: {
    ...lead,
    eligible: true,
    all_in_rate: matchedUtility.rate,
    matched_utility: matchedUtility.name
  }
}];

// Solar Savings Calculator - Complete Implementation
const lead = $input.first().json;
const usageFactors = $input.last().json;

// Get usage intensity factor for lead's industry type
const industryFactor = usageFactors.find(
  f => f.Industry.toLowerCase() === lead.type?.toLowerCase()
);

const intensity = industryFactor?.Usage_Intensity_Factor || 15; // Default 15 kWh/sqft/year

// Constants from requirements
const offsetPercent = 0.93;  // 93% energy offset
const customerDiscount = 0.90; // Customer pays 90% of credits

// Inputs
const totalSqFt = parseFloat(lead.square_footage) || 0;
const allInRate = parseFloat(lead.all_in_rate) || 0.12; // Default $0.12/kWh

// Calculations
const kWhUsage = totalSqFt * intensity;
const annualEnergyCost = kWhUsage * allInRate;
const annualCredits = kWhUsage * offsetPercent;
const customerCost = annualCredits * allInRate * customerDiscount;
const annualSavings = annualEnergyCost - customerCost;
const savingsPercent = (annualSavings / annualEnergyCost) * 100;

return [{
  json: {
    ...lead,
    // Calculation inputs
    usage_intensity_factor: intensity,
    // Calculation results
    kwh_usage: Math.round(kWhUsage),
    annual_energy_cost: Math.round(annualEnergyCost * 100) / 100,
    annual_credits: Math.round(annualCredits),
    customer_cost: Math.round(customerCost * 100) / 100,
    annual_savings: Math.round(annualSavings * 100) / 100,
    savings_percent: Math.round(savingsPercent * 10) / 10,
    calculated_at: new Date().toISOString()
  }
}];

// Lead Scoring & Qualification Logic
const lead = $input.first().json;

let score = 0;
let qualificationNotes = [];

// Scoring criteria
if (lead.annual_savings >= 10000) {
  score += 30;
  qualificationNotes.push('High savings potential (>$10K)');
} else if (lead.annual_savings >= 5000) {
  score += 20;
  qualificationNotes.push('Medium savings potential ($5K-$10K)');
} else {
  score += 10;
  qualificationNotes.push('Low savings potential (<$5K)');
}

// Contact availability
if (lead.contacts && lead.contacts.length > 0) {
  const hasEmail = lead.contacts.some(c => c.email);
  const hasPhone = lead.contacts.some(c => c.phone);

  if (hasEmail) {
    score += 20;
    qualificationNotes.push('Email contact available');
  }
  if (hasPhone) {
    score += 20;
    qualificationNotes.push('Phone contact available');
  }
} else {
  qualificationNotes.push('No contact information');
}

// Utility eligibility already validated
score += 20;
qualificationNotes.push('Eligible utility provider');

// Manual review flag penalty
if (lead.needs_manual_review) {
  score -= 10;
  qualificationNotes.push('Needs manual review for square footage');
}

// Qualification threshold: 60+
const qualified = score >= 60;

return [{
  json: {
    ...lead,
    lead_score: score,
    qualified: qualified,
    qualification_notes: qualificationNotes.join('; '),
    status: qualified ? 'Qualified' : 'Enriched'
  }
}];

// Prepare contact records for Airtable
const lead = $input.first().json;
const companyRecord = lead; // Has Airtable record ID

if (!lead.contacts || lead.contacts.length === 0) {
  return [{ json: { message: 'No contacts to create', company_id: companyRecord.id } }];
}

return lead.contacts.map(contact => ({
  json: {
    name: contact.name,
    title: contact.title,
    email: contact.email,
    phone: contact.phone,
    company_id: companyRecord.id,
    verified: false,
    role_tag: contact.title?.toLowerCase().includes('facilities') ? 'Facilities' :
              contact.title?.toLowerCase().includes('sustainability') ? 'Sustainability' : 'Other'
  }
}));

