// Extract email content from OpenAI response
const response = $input.first().json;
const emailContent = response.choices?.[0]?.message?.content || '';

// Parse subject and body (assuming format: Subject: ... \n\nBody...)
const subjectMatch = emailContent.match(/Subject:\s*(.+?)\n/i);
const subject = subjectMatch ? subjectMatch[1].trim() : 'Exclusive Community Solar Savings for ' + $json.prospect_data.company_name;

// Remove subject line from body
const body = emailContent.replace(/Subject:\s*.+?\n\n?/i, '').trim();

return [{
  json: {
    ...$json,
    email_subject: subject,
    email_body: body,
    email_generated_at: new Date().toISOString()
  }
}];

// Prepare Vapi voice call script
const lead = $input.first().json;
const prospect = lead.prospect_data;

const callScript = `
Hi, this is Sarah from Community Solar Solutions. 

I'm calling for ${prospect.contact_name} at ${prospect.company_name}.

I've been researching ${prospect.type.toLowerCase()}s in ${prospect.city}, and I found that you could save approximately ${prospect.annual_savings.toLocaleString()} annually on your energy costs through our community solar program.

That's about ${prospect.savings_percent}% off your current electricity bill, with zero upfront costs and no rooftop installation needed.

Would you have 15 minutes this week to discuss how this works? I can show you the exact numbers for your ${prospect.square_footage.toLocaleString()} square foot facility.

[PAUSE FOR RESPONSE]

If interested: Great! I'll send you a calendar link to book a time that works for you.

If not interested: No problem. Can I follow up via email with more details?

If voicemail: Hi ${prospect.contact_name}, this is Sarah from Community Solar Solutions calling about potential energy savings for ${prospect.company_name}. We've identified approximately ${prospect.annual_savings.toLocaleString()} in annual savings opportunities. I'll send details via email, but feel free to call me back at 555-0100. Thanks!
`;

return [{
  json: {
    ...lead,
    call_script: callScript,
    call_to_number: lead.contacts?.[0]?.phone || '',
    call_from_number: '+15550100',
    contact_name: prospect.contact_name
  }
}];

