// Process Vapi webhook event
const event = $input.first().json;

const eventType = event.type;
const callId = event.call?.id;
const companyId = event.call?.metadata?.company_id;
const callStatus = event.call?.status;
const duration = event.call?.duration;
const intent = event.call?.analysis?.intent; // positive, negative, neutral
const transcript = event.call?.transcript;

let statusUpdate = 'Contacted';
let notes = '';

switch(eventType) {
  case 'call.answered':
    statusUpdate = 'Contacted - Call Answered';
    notes = 'Call answered, conversation in progress';
    break;
  case 'call.voicemail':
    statusUpdate = 'Contacted - Voicemail';
    notes = 'Voicemail left';
    break;
  case 'call.ended':
    if (intent === 'positive') {
      statusUpdate = 'Interested';
      notes = `Call completed. Positive intent detected. Duration: ${duration}s`;
    } else if (intent === 'negative') {
      statusUpdate = 'Not Interested';
      notes = `Call completed. Not interested. Duration: ${duration}s`;
    } else {
      statusUpdate = 'Contacted';
      notes = `Call completed. Neutral response. Duration: ${duration}s`;
    }
    break;
}

return [{
  json: {
    company_id: companyId,
    call_id: callId,
    event_type: eventType,
    status_update: statusUpdate,
    call_duration: duration,
    call_intent: intent,
    call_notes: notes,
    call_transcript: transcript,
    processed_at: new Date().toISOString()
  }
}];

// Process Calendly booking webhook
const event = $input.first().json;

const eventType = event.event;
const invitee = event.payload?.invitee;
const scheduledEvent = event.payload?.scheduled_event;

const inviteeEmail = invitee?.email;
const inviteeName = invitee?.name;
const eventStartTime = scheduledEvent?.start_time;
const eventEndTime = scheduledEvent?.end_time;
const eventUri = scheduledEvent?.uri;
const cancelUrl = invitee?.cancel_url;
const rescheduleUrl = invitee?.reschedule_url;

return [{
  json: {
    event_type: eventType,
    invitee_email: inviteeEmail,
    invitee_name: inviteeName,
    meeting_start: eventStartTime,
    meeting_end: eventEndTime,
    meeting_uri: eventUri,
    cancel_url: cancelUrl,
    reschedule_url: rescheduleUrl,
    processed_at: new Date().toISOString()
  }
}];

